document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll("pre > code").forEach((e,t)=>{const o=e.parentNode,s=document.createElement("div");s.style.position="relative",s.className="highlight-wrapper";const n=document.createElement("button");n.className="copy-code-button text-red-700 font-bold py-1 px-2 rounded absolute top-0 right-0 border-dashed border border-black -mt-10",n.textContent="Copy",n.addEventListener("click",()=>{navigator.clipboard.writeText(e.textContent).then(()=>{console.log(`Code block ${t+1} copied.`),n.textContent="Copied!",setTimeout(()=>{n.textContent="Copy"},2e3)}).catch(e=>console.error(`Failed to copy code block ${t+1}:`,e))}),o.parentNode&&(o.parentNode.insertBefore(s,o),s.appendChild(o),s.appendChild(n))})})