document.addEventListener("DOMContentLoaded",()=>{console.log("DOM fully loaded. Searching for code blocks..."),document.querySelectorAll("pre > code").forEach((e,t)=>{console.log(`Processing code block ${t+1}`);const n=document.createElement("button");n.className="copy-code-button bg-blue-500 hover:bg-blue-700 text-white font-bold py-1 px-2 rounded absolute top-0 right-0 m-2",n.textContent="Copy",n.style.position="absolute",n.style.top="0.5rem",n.style.right="0.5rem",n.addEventListener("click",()=>{navigator.clipboard.writeText(e.textContent).then(()=>{console.log(`Code block ${t+1} copied.`),n.textContent="Copied!",setTimeout(()=>{n.textContent="Copy"},2e3)}).catch(e=>console.error(`Failed to copy code block ${t+1}:`,e))});const s=e.parentNode;s&&s.style?(s.style.position="relative",s.appendChild(n),console.log(`Button added to code block ${t+1}`)):console.log(`Failed to add button to code block ${t+1}: No parent node found.`)})})