function toggleLanguage(){var e=document.getElementById("japanese-text"),t=document.getElementById("english-text"),n=document.getElementById("language-toggle").checked;n?(e.style.display="none",t.style.display="block"):(e.style.display="block",t.style.display="none")}const japaneseCharacters=["仕事","壮大","木","切磋琢磨","侘寂","和"];function changeJapaneseText(){const e=Math.floor(Math.random()*japaneseCharacters.length);document.getElementById("japanese-char").textContent=japaneseCharacters[e]}setInterval(changeJapaneseText,100)